# Para usar o azure command line, instale o azure cli: https://azure.microsoft.com/pt-br/documentation/articles/xplat-cli/

# Para deletar as máquinas.
azure vm delete usp-master-01
azure vm delete usp-slave-11
azure vm delete usp-slave-12
azure vm delete usp-slave-13

# Para criar as máquinas utilizando uma pem e vpn existente.
azure vm create usp-master-01 "b39f27a8b8c64d52b05eac6a62ebad85__Ubuntu-14_04_3-LTS-amd64-server-20150805-en-us-30GB" --ssh-cert usp.pem --userName usp  --password "Usp2015@" --location "East US" --virtual-network-name "usp-vpnet" --vm-size "Basic_A3" --ssh 22
azure vm create usp-slave-11 "b39f27a8b8c64d52b05eac6a62ebad85__Ubuntu-14_04_3-LTS-amd64-server-20150805-en-us-30GB" --ssh-cert usp.pem --userName usp  --password "Usp2015@" --location "East US"  --virtual-network-name "usp-vpnet" --vm-size "Basic_A2" --ssh 22
azure vm create usp-slave-12 "b39f27a8b8c64d52b05eac6a62ebad85__Ubuntu-14_04_3-LTS-amd64-server-20150805-en-us-30GB" --ssh-cert usp.pem --userName usp  --password "Usp2015@" --location "East US" --virtual-network-name "usp-vpnet" --vm-size "Basic_A2" --ssh 22
azure vm create usp-slave-13 "b39f27a8b8c64d52b05eac6a62ebad85__Ubuntu-14_04_3-LTS-amd64-server-20150805-en-us-30GB" --ssh-cert usp.pem --userName usp  --password "Usp2015@" --location "East US" --virtual-network-name "usp-vpnet" --vm-size "Basic_A2" --ssh 22

